mapper.update:
UPDATE study

     study_user_id= #{study.studyUserId,jdbcType=VARCHAR},
     study_user_name= #{study.studyUserName,jdbcType=VARCHAR},
     study_start_time= #{study.studyStartTime,jdbcType=TIMESTAMP},
     study_end_time= #{study.studyEndTime,jdbcType=TIMESTAMP}

WHERE study_seat_id = #{study.studySeatId,jdbcType=VARCHAR}
